'use strict';

/**
* Parse dsv incrementally.
*
* @module @stdlib/utils/parse-dsv
*
* @example
* var Buffer = require( 'buffer' ).Buffer;
* var parse_dsv_stream = require( '@stdlib/utils/parse-dsv' )
* var parse_dsv = parse_dsv_stream();
*
* var csv = parse_dsv(Buffer.from('1,2,3'));
* // returns [ [ "1", "2", "3" ] ]
*
* csv = parse_dsv( Buffer.from( '\n' ) );
* // returns [ [ "1", "2", "3" ] ]
*
* csv = parse_dsv( Buffer.from('4, 5') );
* // returns [ [ "1", "2", "3" ], [ "4", " 5" ] ]
*
* csv = parse_dsv( Buffer.from( ', 6' ))
* // returns [ [ "1", "2", "3" ], [ "4", " 5", " 6" ] ]
*/

// MODULES //

var parseDsv = require( './main.js' );


// EXPORTS //

module.exports = parseDsv;
